const WordCloud = require('wordcloud');

const title = "To Kill a Mockingbird";
const words = title.split(' ');

const options = {
  width: 400,
  height: 300,
  wordSpacing: 10,
  fontFamily: 'Arial',
  fontSize: 16,
  fontWeight: 'bold',
  colors: ['#3498db', '#e74c3c', '#2ecc71', '#9b59b6']
};

WordCloud(words, options).toFile('./wordcloud.png', function(err) {
  if (err) {
    console.log(err);
  } else {
    console.log('Word cloud generated successfully!');
  }
});
```
