**index.html**
```html
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <title>Flappy Bird</title>
  <style>
    /* Add some basic styling to our game */
    body {
      font-family: Arial, sans-serif;
      background-color: #f0f0f0;
    }
    .bird {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      width: 30px;
      height: 30px;
      background-color: #ff69b4;
      border-radius: 50%;
      cursor: pointer;
    }
    .pipe {
      position: absolute;
      width: 50px;
      height: 200px;
      background-color: #666;
      border-radius: 10px;
    }
  </style>
</head>
<body>
  <canvas id="game" width="400" height="600"></canvas>
  <script src="script.js"></script>
</body>
</html>
```
**script.js**
```javascript
// Get the canvas element
const canvas = document.getElementById('game');
const ctx = canvas.getContext('2d');

// Set the canvas dimensions
canvas.width = 400;
canvas.height = 600;

// Define some constants
const BIRD_WIDTH = 30;
const BIRD_HEIGHT = 30;
const PIPE_WIDTH = 50;
const PIPE_HEIGHT = 200;
const GRAVITY = 0.5;
const JUMP_HEIGHT = 10;

// Define the bird object
let bird = {
  x: canvas.width / 2,
  y: canvas.height / 2,
  vx: 0,
  vy: 0,
  radius: BIRD_WIDTH / 2,
  jumping: false
};

// Define the pipe objects
let pipes = [];

// Main game loop
function update() {
  // Update the bird's position
  bird.x += bird.vx;
  bird.y += bird.vy;

  // Apply gravity
  bird.vy += GRAVITY;

  // Check for collisions with the ground
  if (bird.y + bird.radius > canvas.height) {
    bird.y = canvas.height - bird.radius;
    bird.vy = 0;
    bird.jumping = false;
  }

  // Check for collisions with the pipes
  for (let i = 0; i < pipes.length; i++) {
    const pipe = pipes[i];
    if (checkCollision(bird, pipe)) {
      // Game over!
      alert('Game Over!');
      return;
    }
  }

  // Update the pipes
  for (let i = 0; i < pipes.length; i++) {
    const pipe = pipes[i];
    pipe.x -= 2;
    if (pipe.x < -PIPE_WIDTH) {
      pipes.splice(i, 1);
    }
  }

  // Add new pipes
  if (Math.random() < 0.1) {
    pipes.push({
      x: canvas.width,
      y: Math.random() * (canvas.height - PIPE_HEIGHT)
    });
  }

  // Draw everything
  ctx.clearRect(0, 0, canvas.width, canvas.height);
  ctx.fillStyle = 'rgba(255, 0, 0, 0.5)';
  ctx.fillRect(bird.x, bird.y, BIRD_WIDTH, BIRD_HEIGHT);
  for (let i = 0; i < pipes.length; i++) {
    const pipe = pipes[i];
    ctx.fillStyle = 'rgba(0, 0, 0, 0.5)';
    ctx.fillRect(pipe.x, pipe.y, PIPE_WIDTH, PIPE_HEIGHT);
  }
}

// Check for collisions between two objects
function checkCollision(obj1, obj2) {
  const dx = obj1.x - obj2.x;
  const dy = obj1.y - obj2.y;
  const distance = Math.sqrt(dx * dx + dy * dy);
  return distance < obj1.radius + obj2.radius;
}

// Handle user input
document.addEventListener('keydown', (event) => {
  if (event.key === ' ') {
    bird.jumping = true;
    bird.vy = -JUMP_HEIGHT;
  }
});

// Main game loop
setInterval(update, 16); // 16ms = 60fps
```
